option.value:
import { observer } from "mobx-react-lite";
import { useEffect } from "react";

import { useCustomVideo } from "@/hooks/useCustomVideo.ts";
import { useVideoSettings } from "@/hooks/useVideoSettings.ts";

type VideoConfigLoaderProps = {
  originalStream: MediaStream | null;
  gameId: string;
  onNeedConfig: () => void;
};

export const VideoConfigLoader = observer(
  ({ originalStream, gameId, onNeedConfig }: VideoConfigLoaderProps) => {
    const { getSavedSettings } = useVideoSettings(gameId);
    const { applySettings } = useCustomVideo(originalStream);

    useEffect(() => {
      if (!originalStream) return;

      const savedSettings = getSavedSettings();

      if (savedSettings) {
        applySettings(savedSettings);
      } else {
        onNeedConfig();
      }
    }, [originalStream, getSavedSettings, applySettings, onNeedConfig]);

    return null;
  }
);

VideoConfigLoader.displayName = "VideoConfigLoader";
